replace_lane.operation:
def S.replace_lane(a, i, x):
  result = S.New()
  for j in range(S.Lanes):
      result[j] = a[j]
  result[i] = x
  return result

splat.operation:
def S.splat(x):
  result = S.New()
  for i in range(S.Lanes):
      result[i] = S.Reduce(x)
  return result

swizzle.operation:
def S.swizzle(a, s):
    result = S.New()
    for i in range(S.Lanes):
        if s[i] < S.lanes:
            result[i] = a[s[i]]
        else:
            result[i] = 0
    return result
